func TestVerifyMerkleProof(t *testing.T) {
	for i := 0; i < len(messages); i++ {
		message, err := hex.DecodeString(messages[i])
		require.NoError(t, err)

		metadata, err := hex.DecodeString(metadatas[i])
		require.NoError(t, err)

		result := keeper.VerifyMerkleProof(metadata, message)
		require.True(t, result)
	}
}

func TestVerifyValidatorSignatures(t *testing.T) {
	val1, err := hex.DecodeString("bcb815f38d481a5eba4d7ac4c9e74d9d0fc2a7e7")
	if err != nil {
		fmt.Println("err decode val1")
	}
	val2, err := hex.DecodeString("d839424e2e5ace0a81152298dc2b1e3bb3c7fb20")
	if err != nil {
		fmt.Println("err decode val2")
	}
	val3, err := hex.DecodeString("b8085c954b75b7088bcce69e61d12fcef797cd8d")
	if err != nil {
		fmt.Println("err decode val3")
	}
	val4, err := hex.DecodeString("b8085c954b75b7088bcce69e61d12fcef797cd8d")
	if err != nil {
		fmt.Println("err decode val3")
	}
	valPubKeys := [][]byte{
		val1,
		val2,
		val3,
		val4,
	}
	ism := keeper.MultiSigIsm{
		ValPubKeys: valPubKeys,
		Threshold: uint32(4),
	}
	message, err := hex.DecodeString(messages[9])
	require.NoError(t, err)

	metadata, err := hex.DecodeString(metadatas[9])
	require.NoError(t, err)

	result := keeper.VerifyValidatorSignatures(metadata, message, ism)
	require.True(t, result)
}